import { React, useState } from "react";
import { useDispatch } from "react-redux";
import { createProduct, loadProducts } from "../../../redux/productReducer";
import { useNavigate, useParams, Link } from "react-router-dom";
import { Form, Input, Button, Select, InputNumber } from "antd";
import { Image } from "antd";
import "./createProduct.css";

const { TextArea } = Input;
const { Search } = Input;

const CreateProduct = () => {
  const [url, setUrl] = useState("");
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const onFinish = (values) => {
    dispatch(createProduct(values));
    navigate("/products");
  };

  const onSearch = (values) => {
    setUrl(values);
  };

  const handleBackToHome = () => {
    dispatch(loadProducts());
    setTimeout(() => {
      navigate("/products");
    }, 200);
  };

  return (
    <div className="create-product">
      <div className="product-details-header">
        <h2>Create Product</h2>
        <Button onClick={handleBackToHome}>Back to Home</Button>
      </div>

      <Form className="product-form" layout="vertical" onFinish={onFinish}>
        <Form.Item
          label="Product Name"
          required
          name="productName"
          rules={[{ required: true, message: "Product Name can't be blank!" }]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label="Product Description"
          required
          name="productDescription"
          rules={[
            { required: true, message: "Product Description can't be blank!" },
          ]}
        >
          <TextArea rows={4} />
        </Form.Item>
        <Form.Item
          label="Category"
          required
          name="category"
          rules={[{ required: true, message: "Category can't be blank!" }]}
        >
          <Select>
            <Select.Option value="Electronics" key="Electronics">
              Electronics
            </Select.Option>
            <Select.Option value="Home" key="Home">
              Home
            </Select.Option>
            <Select.Option value="Toy" key="Toy">
              Toy
            </Select.Option>
            <Select.Option value="Office Supply" key="Office Supply">
              Office Supply
            </Select.Option>
            <Select.Option value="Furniture" key="Furniture">
              Furniture
            </Select.Option>
          </Select>
        </Form.Item>
        <div id="price-qty">
          <Form.Item
            label="Price"
            required
            name="price"
            rules={[{ required: true, message: "Price Name can't be blank!" }]}
          >
            <InputNumber />
          </Form.Item>
          <Form.Item
            label="In Stock Quantity"
            required
            name="inStockQuantity"
            rules={[
              {
                required: true,
                message: "In Stock Quantity Name can't be blank!",
              },
            ]}
          >
            <InputNumber />
          </Form.Item>
        </div>
        <Form.Item
          label="Add Image Link"
          required
          name="url"
          rules={[{ required: true, message: "Image URL can't be blank!" }]}
        >
          <Search
            placeholder="http://"
            allowClear
            enterButton="Preview"
            onSearch={onSearch}
          />
        </Form.Item>

        <Image
          width={200}
          height={200}
          src={url || "error"}
          fallback="data:image/png;base64,"
        />
        <Form.Item>
          <Button htmlType="submit">Add Product</Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default CreateProduct;
